const pokemonUI = (function () {
  let modalElements = { name: document.getElementById("pokemonName"), img: document.getElementById("pokemonImg"), type: document.getElementById("pokemonType"), height: document.getElementById("pokemonHeight"), }; function createElementWithClass(tag, classList) { let element = document.createElement(tag); if (classList) element.classList.add(...classList); return element }
  function populatePokemonList(pokemonList, listElement) { listElement.innerHTML = ""; pokemonList.forEach(function (pokemon) { let listItem = createListItem(pokemon); listElement.appendChild(listItem) }) }
  function createListItem(pokemon) { let listItem = createElementWithClass('div', ["col-12", "col-sm-6", "col-md-4", "col-lg-2"]); let nameCard = createElementWithClass('div', ["card", "text-center", "shadow-sm"]); let cardBody = createElementWithClass('div', ["card-body"]); let nameButton = createElementWithClass('div', ["btn", "btn-primary", "w-100"]); nameButton.textContent = pokemon.name; nameButton.addEventListener("click", function () { pokemonRepository.loadDetails(pokemon).then(function () { showModal(pokemon) }) }); cardBody.appendChild(nameButton); nameCard.appendChild(cardBody); listItem.appendChild(nameCard); return listItem }
  function showModal(pokemon) { modalElements.name.textContent = pokemon.name; modalElements.img.src = pokemon.imgUrl; modalElements.img.alt = pokemon.name; modalElements.type.textContent = `Type: ${pokemon.type.join(', ')}`; modalElements.height.textContent = `Height: ${pokemon.height}m`; let pokemonModal = new bootstrap.Modal(document.getElementById("pokemonModal")); pokemonModal.show() }
  return { populatePokemonList }
})()